mapper.update:
UPDATE MEMBER
SET NAME = #{name}, PHONE = #{phone}
WHERE USER_ID = #{user_id}

UPDATE MEMBER M JOIN BUSINESS B
ON M.MEMBER_NO=B.MEMBER_NO
SET NAME =#{name}, PHONE=#{phone}, COMPANY=#{company}, POSTCODE = #{postcode}, ADDRESS1 = #{address1}, 
ADDRESS2 = #{address2}, BUSINESS_PHONE = #{business_phone}, BUSINESS_NUMBER = #{business_number}
WHERE USER_ID = #{user_id}

UPDATE MEMBER
SET PASSWORD = #{password}
WHERE USER_ID = #{user_id}

mapper.select:
SELECT USER_ID
FROM MEMBER
WHERE EMAIL = #{email}
